create database Reis;
use Reis;

create table reisid (
	reisID INT,
	Aeg VARCHAR(50),
	Alguskuupaev DATE,
	Loppkuupaev DATE,
	Lennujaama VARCHAR(50),
	Riik VARCHAR(50),
	Linn VARCHAR(50),
	Hind VARCHAR(50)
);


SELECT 
    reisID AS '@reisID',
    Aeg AS '@Aeg',
    Hind AS '@Hind',
    (SELECT 
        Alguskuupaev AS 'Alguskuupaev',
        Loppkuupaev AS 'Loppkuupaev'
     FOR XML PATH('Kuupaevad'), TYPE),
    (SELECT 
        Riik AS 'Riik',
        Linn AS 'Linn'
     FOR XML PATH('Sihtkoht'), TYPE),
    (SELECT 
        Lennujaama AS 'Lennujaam'
     FOR XML PATH('Transport'), TYPE)
FROM reisid
FOR XML PATH('Reis'), ROOT('Reisid');
